import 'package:flutter/foundation.dart';import 'package:flutter/material.dart';import 'package:news/state_management/bloc_home_news_list/news_home_bloc_provider_statefulWidget.dart';import '../../model/news_model.dart';import '../../small_component/news_list_view.dart';import '../../small_component/page_number_action_view.dart';import '../../small_component/published_dropdown_view.dart';import '../../small_component/top_main_filter_view.dart';import '../../state_management/bloc_home_news_list/news_home_bloc.dart';import '../screen_menu.dart';import '../search_page.dart';import '../top_trend_page.dart';class MyHomePageWithBloc extends StatefulWidget {  const MyHomePageWithBloc({super.key});  @override  State<StatefulWidget> createState() => MyHomePageWithBlocState();}class MyHomePageWithBlocState extends State<MyHomePageWithBloc> {  @override  void dispose() {    super.dispose();    if (kDebugMode) {      print("dispose bloc");    }  }  @override  void initState() {    super.initState();    print("Init state 11");  }  @override  Widget build(BuildContext context) {    if (kDebugMode) {      print("rebuild widget bloc");    }    final ListNewsBloc bloc = NewsBlocProvider.of(context);    return      DefaultTabController(          initialIndex: 0,          length: 3,          child: Scaffold(            drawer: LeftMenu(callback: (action){              //            }),            appBar:            AppBar(                elevation: 0,              title: const Text("Home with provider"),              actions: [IconButton(onPressed: () async {                var result = await showSearch(                    context: context,                    delegate: CustomSearchDelegate()                );              }, icon: const Icon(Icons.search))],              bottom:              TabBar(                  indicator: BoxDecoration(                    borderRadius: BorderRadius.circular(                      1.0,                    ),                    color: Colors.grey,                  ),                  labelColor: Colors.white,                  unselectedLabelColor: Colors.black,                onTap: (index) {                },                  tabs: const [                    Tab(                    child: Text("All News")                ),                    Tab(                    child: Text("Top News")                ),                    Tab(                        child: Text("Technicals")                    )              ])            ),            body:            TabBarView(              children: [                Container(                    color: Colors.grey,                    padding: const EdgeInsets.all(20),                    child:                    Column(                      children: [                        PageNumberActionView(                            prevCallback: (){                              bloc.toPreviousPage();                            },                            nextCallback: (){                              bloc.toNextPage();                            },                            pagesNumCallback: (page){                              bloc.fetchListNewsByPage(page);                            }),                        Row(children: [                          const Spacer(),                          DropdownCustom(dropdownFilterCallback: (filterState) {                            bloc.fetchListNewsBySortBy(filterState);                          },)                        ]),                        allNews()                      ],                    )                ),                topListNews(context),                topListNews(context)              ],            )          )      );  }  Widget topListNews(BuildContext context) {    final ListNewsBloc bloc = NewsBlocProvider.of(context);    return      Container(        padding: const EdgeInsets.all(20),        color: Colors.grey,        child: StreamBuilder<List<News>>(            initialData: [],            stream: bloc.listNewsStream.stream,            builder: (context, snapshot) {              if (snapshot.hasError) {                return const Center(child: Text("There are something wrong"));              }              if (snapshot.hasData) {                var list = snapshot.data ?? [];                return NewsListView(list: list);              }              else {                return const CircularProgressIndicator();              }            }),      );  }  Widget allNews() {    return      Expanded(child: StreamBuilder<List<News>>(          initialData: [],          // stream: listNewsBloc.listNewsStream.stream,          builder: (context, snapshot) {            if (kDebugMode) {              print("call stream builder again");            }            if (snapshot.hasError) {              return const Center(child: Text("There are something wrong"));            }            if (snapshot.hasData) {              var list = snapshot.data ?? [];              return NewsListView(list: list);            }            else {              return const CircularProgressIndicator();            }          }));  }}