import 'package:flutter/foundation.dart';import 'package:flutter/material.dart';import 'package:news/screens/screen_menu.dart';import 'package:news/small_component/page_number_action_view.dart';import '../small_component/published_dropdown_view.dart';import '../small_component/top_main_filter_view.dart';class MyHomePage extends StatefulWidget {  const MyHomePage({super.key, required this.title});  final String title;  @override  State<MyHomePage> createState() => _MyHomePageState();}class _MyHomePageState extends State<MyHomePage> {  void topTrend() {    if (kDebugMode) {      print("top trend");    }  }  void allNews() {    if (kDebugMode) {      print("all news");    }  }  void prev() {    if (kDebugMode) {      print("prev");    }  }  void next() {    if (kDebugMode) {      print("next");    }  }  void pagesNum(int page) {    if (kDebugMode) {      print("page nums: $page");    }  }  @override  Widget build(BuildContext context) {    return Scaffold(      drawer: const LeftMenu(),      appBar: AppBar(        title: Text(widget.title),        actions: [IconButton(onPressed: (){          if (kDebugMode) {            print("Search start");          }        }, icon: const Icon(Icons.search))],      ),      body: Padding(          padding: const EdgeInsets.all(20),          child: Column(            children: <Widget>[              TopMainFilterView(allNewsCallback: allNews, topTrendingCallback: topTrend),              PageNumberActionView(prevCallback: prev, nextCallback: next, pagesNumCallback: pagesNum),              Row(children: const [                Spacer(),                DropdownCustom()              ]),              Container(height: 20),              Expanded(                  child: ListView.builder(                      scrollDirection: Axis.vertical,                      itemCount: 30,                      itemBuilder: (context, index) {                        return const Text("News");                      })              )            ],      )),    );  }}