import 'package:flutter/material.dart';import 'package:news/api_client/api_client.dart';import 'package:news/small_component/news_list_view.dart';import 'package:news/small_component/published_dropdown_view.dart';import '../model/news_model.dart';class TopTrend extends StatefulWidget {  final APIClient apiClient = APIClient();  TopTrend({super.key});  @override  State<StatefulWidget> createState() => TopTrendState();}class TopTrendState extends State<TopTrend> {  @override  void initState() {    // TODO: implement initState    super.initState();  }  @override  Widget build(BuildContext context) {    // TODO: implement build    return      Container(        child: FutureBuilder<List<News>>(          future: widget.apiClient.fetchTopNews(),            builder: (context, snapshot) {              if (snapshot.hasError) {                return const Center(child: Text("Have an error"));              } else if (snapshot.hasData) {                return NewsListView(list: snapshot.data!);              } else {                return const CircularProgressIndicator();              }        }),      );  }}