import 'package:flutter/foundation.dart';import 'package:flutter/material.dart';import 'package:news/small_component/row_news_view.dart';import '../model/news_model.dart';import '../screens/detail_page.dart';class NewsListView extends StatefulWidget {  List<News> list = [];  NewsListView({super.key, required this.list});  @override  State<StatefulWidget> createState() => NewsListState();}class NewsListState extends State<NewsListView> {  @override  Widget build(BuildContext context) {    // TODO: implement build    return Expanded(        flex: 1,        child: ListView.separated(          scrollDirection: Axis.vertical,          itemCount: 10,          itemBuilder: (context, index) {            return GestureDetector(              child: const RowNews(),              onTap: (){                if (kDebugMode) {                  print("index: $index");                }                Navigator.push(context, MaterialPageRoute(builder: (context) => DetailPage()));              },            );          }, separatorBuilder: (BuildContext context, int index) {          return Container(height: 8,);        },)    );  }}