import 'package:flutter/foundation.dart';import 'package:flutter/material.dart';import 'package:news/small_component/row_news_view.dart';import '../model/news_model.dart';import '../screens/detail_page.dart';class NewsListView extends StatelessWidget {  final List<News> list;  const NewsListView({super.key, required this.list});  @override  Widget build(BuildContext context) {    // TODO: implement build    return ListView.separated(      scrollDirection: Axis.vertical,      itemCount: list.length,      itemBuilder: (context, index) {        News news = list[index];        return GestureDetector(          child: RowNews(news: news),          onTap: (){            if (kDebugMode) {              print("index: $index");            }            Navigator.push(context, MaterialPageRoute(builder: (context) => DetailPage(news: news)));          },        );      }, separatorBuilder: (BuildContext context, int index) {      return Container(height: 8,);    });  }}