import 'dart:ffi';import 'dart:ui';import 'package:flutter/material.dart';class PageNumberActionView extends StatelessWidget {  const PageNumberActionView({super.key, required this.prevCallback, required this.pagesNumCallback, required this.nextCallback});  final VoidCallback prevCallback;  final VoidCallback nextCallback;  final Function(int) pagesNumCallback;  @override  Widget build(BuildContext context) {    // TODO: implement build    return SizedBox(      height: 20,      child: Row(        mainAxisAlignment: MainAxisAlignment.start,          children: [            Expanded(              flex: 1, child: MaterialButton(onPressed: prevCallback, child: const Text("Prev"))            ),            Container(width: 5),            Expanded(              flex: 3,                child: ListView.builder(                scrollDirection: Axis.horizontal,                itemCount: 7,                itemBuilder: (context, index) {                  return                    SizedBox(                      width: 30,                        child: MaterialButton(onPressed: () {                          pagesNumCallback(index);                        }, child: Text("$index")));                },                )            ),            Container(width: 5),            Expanded(child: MaterialButton(onPressed: nextCallback, child: const Text("Next"))            ),          ]      ),    );        /*         */  }}