import 'package:flutter/material.dart';class DropdownCustom extends StatefulWidget {  const DropdownCustom({super.key});  @override  State<StatefulWidget> createState() {    // TODO: implement createState    return DropdownState();  }}List<String> list = ["Publish at", "Relevant", "Popularity"];class DropdownState extends State<DropdownCustom> {  String selected = list.first;  @override  Widget build(BuildContext context) {    // TODO: implement build    return DropdownButton<String>(      value: selected,      onChanged: (value){        print("change");        setState(() {          selected = value!;        });      }, items: list.map<DropdownMenuItem<String>>((String item) {      return DropdownMenuItem(value: item, child: Text(item));    }    ).toList());  }}