import 'package:flutter/material.dart';enum DropdownFilterState {  publishAt,  relevant,  popularity;}class DropdownCustom extends StatefulWidget {  final Function(DropdownFilterState) dropdownFilterCallback;  const DropdownCustom({super.key, required this.dropdownFilterCallback});  @override  State<StatefulWidget> createState() {    return DropdownState();  }}List<DropdownFilterState> list = [  DropdownFilterState.publishAt,  DropdownFilterState.relevant,  DropdownFilterState.popularity];class DropdownState extends State<DropdownCustom> {  DropdownFilterState selected = DropdownFilterState.publishAt;  @override  Widget build(BuildContext context) {    return DropdownButton<DropdownFilterState>(      value: selected,      onChanged: (value){        setState(() {          selected = value!;        });        widget.dropdownFilterCallback(selected);      },        items:        list.map<DropdownMenuItem<DropdownFilterState>>(                (DropdownFilterState item) {             return DropdownMenuItem(value: item, child: Text(item.name));        }    ).toList());  }}