import 'package:flutter/material.dart';import '../model/news_model.dart';class RowNews extends StatelessWidget {  final News news;  const RowNews({    super.key,    required this.news  });  @override  Widget build(BuildContext context) {    // TODO: implement build    return      Stack(        children: [          Positioned(right: 0, bottom: 0, child: Container(width: 50, height: 50, color: Colors.yellow,)),          Positioned(left: 0, top: 0, child: Container(width: 50, height: 50, color: Colors.yellow,)),          Container(            color: Colors.white,            margin: const EdgeInsets.all(5),            child:              Row(                crossAxisAlignment: CrossAxisAlignment.center,                children: [                  Expanded(                      flex: 1,                      child: SizedBox(                        width: 100,                        height: 100,                        child: FadeInImage(                            fit: BoxFit.fill,                            image: news.imageURL == null                                ? const AssetImage('assets/images/news.jpg') as ImageProvider :                            NetworkImage(news.imageURL!),                            placeholder: const AssetImage("assets/images/news.jpg")                        ),                      )                  ),                  Expanded(                      flex: 2,                      child: Column(                          mainAxisAlignment: MainAxisAlignment.spaceEvenly,                          crossAxisAlignment: CrossAxisAlignment.start,                          children: [                            Row(children: [                              const Spacer(),                              Expanded(flex: 15,                                  child: Text(news.title ?? "No title")                              )                            ]),                            Container(height: 5),   