// this provider base on inherited widgetimport 'package:flutter/material.dart';import 'package:news/state_management/bloc_home_news_list/news_home_bloc.dart';class NewsBlocProviderInherited extends InheritedWidget {  final Bloc bloc = ListNewsBloc();  NewsBlocProviderInherited({    super.key,    required super.child  });  static of<T extends Bloc>(BuildContext context) {    final NewsBlocProviderInherited providerInherited = context.findAncestorWidgetOfExactType()!;    return providerInherited.bloc;  }  @override  bool updateShouldNotify(covariant InheritedWidget oldWidget) {    return true;  }}