import 'package:flutter/material.dart';import 'package:news/api_client/api_client.dart';import '../../model/news_model.dart';class NewsListProvider extends ChangeNotifier {  List<News> listNews = [];  final APIClient _client = APIClient();  bool isLoading = true;  Future<void> fetchAllNews() async {    var newsList = await _client.fetchNews();    listNews = newsList;    isLoading = false;    notifyListeners();  }}class PagingProvider extends ChangeNotifier {}